factory.trackFetch = function(absPath) {

  /** OPTIONS (Stars = Required)
   *    absPath: *               serverUrl/track/{id}/{lang}/track.{format}
   *    {id}: *                  Integer
   *    {lang}: *                String          ISO 639-1 || ISO 639-3
   *    {format}: *              String          mp3|opus|mp4
   *//*

  return $.ajax({
    method: 'GET',
    url: absPath
  }).fail( function(xhr) {

    console.log(xhr);

  });

